(function(process) {
  'use strict';
  var _require = require('../../jest-util/build/index');
  const clearLine = _require.clearLine;
  const chalk = require('../../chalk/index');
  const isCI = require('../../is-ci/index');
  const print = (stream) => {
    if (process.stdout.isTTY && !isCI) {
      stream.write(chalk.bold.dim('Determining test suites to run...'));
    }
  };
  const remove = (stream) => {
    if (stream.isTTY && !isCI) {
      clearLine(stream);
    }
  };
  module.exports = {
    print,
    remove
  };
})(require('process'));
