/* */ 
(function(process) {
  'use strict';
  module.exports = function(points, callback) {
    let inside = 0,
        i = points;
    while (i--)
      if (Math.pow(Math.random(), 2) + Math.pow(Math.random(), 2) <= 1)
        inside++;
    callback(null, (inside / points) * 4);
  };
})(require('process'));
